          style={styles.modalButton}
          onPress={toggleModalDeck}
        >
          <Text style={styles.modalButtonText}>Cancel</Text>
        </TouchableOpacity>
      </View>
    </View>
  </Modal>      
      </View>
    
      <Text style={styles.deckTitle}>{selectedDeck.name}</Text>
      <Carousel
        layout="default"
        layoutCardOffset={9}
        ref={isCarousel}
